import 'package:flutter/services.dart';
import 'package:unionchat/global.dart';

class AppLocalService {
  static String appLocalServiceKey =
      '';

  static Future<bool> start() async {
    try {
      const platform = MethodChannel('LOCAL_SERVICE');
      dynamic result = await platform.invokeMethod(
        'startService',
        {'key': appLocalServiceKey},
      );
      // logger.d(result);
      if (result != 150) {
        logger.e('游戏盾启动失败，失败代码：$result');
        return false;
      }
      return true;
    } catch (e) {
      logger.e(e);
      return false;
    }
  }
}
